import React, { useState } from 'react';

const Contact = () => {

    const [data,setData] = useState({
        fullname:'',
        phone:'',
        email:'',
        msg:'',
    });

    const inputEvent = (event) => {
        const {name,value} = event.target;
        setData((preValue) => {
            return{
                ...preValue,
                [name] : value,
            }
        });
    }

    const formSubmit = (e) =>{
        e.preventDefault();
        alert(`My Name is : ${data.fullname}  . My Phone no. : ${data.phone}  My email is : ${data.email}   My Msg  is : ${data.msg}`)
        setData('');
    };

    return (
        <>
            <div className="my-5">
                <h1 className="text-center">Contact Us</h1>
            </div>
            <div className="container contact_div">
                <div className="row">
                    <div className="col-md-6 col-10 mx-auto">
                    <form onSubmit={formSubmit}>
                        <div class="mb-3">
                            <label for="fullname" class="form-label">Full Name</label>
                            <input type="text" class="form-control" id="fulname" name="fullname" value={data.fullname} onChange={inputEvent} placeholder="Enter your full name" />
                        </div>
                        <div class="mb-3">
                            <label for="phone" class="form-label">Phone No.</label>
                            <input type="tel" class="form-control" id="phone" name="phone" value={data.phone} onChange={inputEvent} placeholder="Enter your phone no." />
                        </div>
                        <div class="mb-3">
                            <label for="exampleFormControlInput1" class="form-label">Email address</label>
                            <input type="email" class="form-control" id="exampleFormControlInput1" name="email" value={data.email} onChange={inputEvent} placeholder="Enter your email address" />
                        </div>
                        <div class="mb-3">
                            <label for="exampleFormControlTextarea1" class="form-label">Message</label>
                            <textarea class="form-control" id="exampleFormControlTextarea1" rows="4" name="msg" value={data.msg} onChange={inputEvent}></textarea>
                        </div>
                        <div class="col-12">
                            <button class="btn btn-outline-primary" type="submit">Submit form</button>
                        </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Contact;
